-- REFAZER O CÓDIGO DO CLIENTE
DO
$$
DECLARE 
	R RECORD;
	C INTEGER;
	
	
BEGIN 
	C = 98880;	
	FOR R IN (SELECT ID
			FROM NS.PESSOAS 
			WHERE PESSOA IN 
			('3858308082016073476', '3858308082016073456', '3858308082016073477', '3858308082016073465', 
			'3858308082016073464', '3858308082016073475', '3858308082016073469', '3858308082016073460', 
			'3858308082016073478', '3858308082016073471', '3858308082016073466', '3858308082016073467', 
			'3858308082016073472', '3858308082016073463', '3858308082016073470', '3858308082016073457', 
			'3858308082016073468', '3819514072016110649', '07149144000153', '04203544000101', '88680010014986',
			'40373631000170', '04798185000182', '12210140000173', '33702028000715', '16582305000199', 
			'07460234000160', '962520000123', '886800000123', 'XXXXX')) LOOP


	EXECUTE 'UPDATE NS.PESSOAS SET PESSOA = ''' || C || ''' WHERE ID = ''' || R.ID || ''''; 
	C = C + 1;
	END LOOP;
END
$$;


